import{_ as e,c as a,o as t,a as r}from"./app.7f52e756.js";const f=JSON.parse('{"title":"Craft CMS","description":"","frontmatter":{"head":[["meta",{"name":"description","content":""}]]},"headers":[{"level":2,"title":"Quickstart","slug":"quickstart"},{"level":2,"title":"Vite support","slug":"vite-support"},{"level":2,"title":"Deployment via Deployer","slug":"deployment-via-deployer"},{"level":2,"title":"Connect with community","slug":"connect-with-community"}],"relativePath":"tutorials/cms-and-frameworks/craftcms.md","lastUpdated":1665256763000}'),s={name:"tutorials/cms-and-frameworks/craftcms.md"},o=r(`<h1 id="craft-cms" tabindex="-1">Craft CMS <a class="header-anchor" href="#craft-cms" aria-hidden="true">#</a></h1><div class="info custom-block"><p class="custom-block-title">INFO</p><p>\u{1F6A7} \xA0 This chapter is work in progress (WIP) . Happy to hear <a href="https://github.com/mandrasch/my-ddev-lab/issues" target="_blank" rel="noopener noreferrer">your feedback / ideas</a>!</p></div><h2 id="quickstart" tabindex="-1">Quickstart <a class="header-anchor" href="#quickstart" aria-hidden="true">#</a></h2><p>Based on <a href="https://craftcms.com/docs/getting-started-tutorial/install/" target="_blank" rel="noopener noreferrer">Intro to CraftCMS: Install</a></p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">mkdir my-crafty-site</span></span>
<span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> my-crafty-site/</span></span>
<span class="line"><span style="color:#A6ACCD;">ddev config --project-type=php --php-version=8.0 --docroot=web --create-docroot</span></span>
<span class="line"><span style="color:#A6ACCD;">ddev composer create -y craftcms/craft</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># Are you ready to begin the setup? (yes|no) [no]: yes</span></span>
<span class="line"></span></code></pre></div><p>You need to input the following answers:</p><p><em>TODO: How can the following be automated? Example here? <a href="https://github.com/thomasbendl/craft4-ddev-vite-blueprint/blob/main/Makefile#L8" target="_blank" rel="noopener noreferrer">https://github.com/thomasbendl/craft4-ddev-vite-blueprint/blob/main/Makefile#L8</a> (There is also an own project type coming up in DDEV)</em></p><blockquote><p>Which database driver are you using? <br>(mysql or pgsql) \u2192 mysql (default)<br> Database server name or IP address \u2192 db<br> Database port \u2192 3306 (default)<br> Database username \u2192 db<br> Database password \u2192 db<br> Database name \u2192 db<br> Database table prefix \u2192 (leave blank)</p></blockquote><blockquote><p>Answer yes to the prompt on whether to install Craft now, and answer the remaining prompts as you like. The only one that matters is Site URL, which you should answer with <a href="https://tutorial.ddev.site" target="_blank" rel="noopener noreferrer">https://tutorial.ddev.site</a>.</p></blockquote><p>The above is quoted from <a href="https://craftcms.com/docs/getting-started-tutorial/install/" target="_blank" rel="noopener noreferrer">Intro to CraftCMS: Install</a>.</p><p>Afterwards:</p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">ddev launch</span></span>
<span class="line"></span></code></pre></div><p><strong>TODO:</strong> If you want to make the repository publicly available via GitHub, you might to move the license key to <code>.env</code> <a href="https://github.com/craftcms/craft/issues/42" target="_blank" rel="noopener noreferrer">https://github.com/craftcms/craft/issues/42</a></p><h2 id="vite-support" tabindex="-1">Vite support <a class="header-anchor" href="#vite-support" aria-hidden="true">#</a></h2><p>See example repository, work in progress: <a href="https://github.com/mandrasch/ddev-craftcms-example" target="_blank" rel="noopener noreferrer">https://github.com/mandrasch/ddev-craftcms-example</a></p><h2 id="deployment-via-deployer" tabindex="-1">Deployment via Deployer <a class="header-anchor" href="#deployment-via-deployer" aria-hidden="true">#</a></h2><ul><li><a href="https://dev.to/mandrasch/deploy-craft-cms-with-ddev-deployer-and-ploi-on-hetzner-cloud-part-1-27l2" target="_blank" rel="noopener noreferrer">Deploy Craft CMS with DDEV, Deployer and Ploi on Hetzner Cloud - Part 1</a></li></ul><h2 id="connect-with-community" tabindex="-1">Connect with community <a class="header-anchor" href="#connect-with-community" aria-hidden="true">#</a></h2><p>There is a dedicated #craftcms-Channel on <a href="https://discord.gg/hCZFfAMc5k" target="_blank" rel="noopener noreferrer">DDEV discord</a>!</p>`,19),n=[o];function l(c,i,p,d,h,m){return t(),a("div",null,n)}var b=e(s,[["render",l]]);export{f as __pageData,b as default};
